INSERT INTO `posts` (`id`, `title`, `description`, `img`, `category_id`, `created_at`, `updated_at`, `featured`, `status`) VALUES
(1, 'MÌNH ĐÃ HỌC FRONTEND NHƯ THẾ NÀO ?', '<p>Ch&agrave;o bạn m&igrave;nh t&ecirc;n l&agrave; Trần Bảo Vĩ , hiện tại m&igrave;nh đang l&agrave; sinh vi&ecirc;n năm 3 ng&agrave;nh kỹ thuật phần mềm.</p>\r\n\r\n<p>B&agrave;i viết n&agrave;y m&igrave;nh sẽ chia sẽ con đường học Frontend của m&igrave;nh trong 1 năm qua, m&igrave;nh hi vọng l&agrave; b&agrave;i viết n&agrave;y sẽ tạo động lực cho bạn cố gắn hơn v&agrave; giỏi hơn trong con đường trở th&agrave;nh một Frontend Developer.</p>\r\n\r\n<p>Khi m&igrave;nh trải qua 1 khoảng thời gian năm thứ 1 đại học th&igrave; m&igrave;nh cảm thấy bản th&acirc;n chưa c&oacute; kiến thức g&igrave; về lập tr&igrave;nh v&agrave; cũng chưa t&igrave;m được hướng đi cho bản th&acirc;n n&ecirc;n m&igrave;nh quyết định sẽ thử sức với lập tr&igrave;nh web. L&uacute;c bắt đầu th&igrave; rất l&agrave; kh&oacute; khăn v&agrave; mất động lực v&igrave; phải học rất nhiều kiến thức l&yacute; thuyết kh&oacute; hiểu, c&aacute;c thẻ HTML v&agrave; thuộc t&iacute;nh của CSS qu&aacute; nhiều m&agrave; m&igrave;nh th&igrave; kh&ocirc;ng thể n&agrave;o nhớ hết được.</p>\r\n\r\n<p>Sau một khoảng thời gian học l&yacute; thuyết th&igrave; m&igrave;nh đ&atilde; quyết định l&ecirc;n Youtube t&igrave;m một video hướng dẫn code 1 trang web tĩnh để thực h&agrave;nh theo v&agrave; kết quả th&igrave; m&igrave;nh cũng đ&atilde; ho&agrave;n th&agrave;nh được trang landing page đơn giản ấy, tuy l&agrave; code theo video hướng dẫn nhưng m&igrave;nh cũng rất vui với những sản phẩm m&igrave;nh code ra.</p>\r\n\r\n<p><img src=\"https://i.ibb.co/fpD937W/A-nh-chu-p-ma-n-hi-nh-30.png\" />Một th&aacute;ng sau khi m&igrave;nh code được những trang landing page đơn giản th&igrave; m&igrave;nh bắt đầu tự code ra những trang web tĩnh m&agrave; m&igrave;nh tự thiết kế, khi b&iacute; đến đ&acirc;u th&igrave; m&igrave;nh search đến đ&oacute;, những trang web m&agrave; m&igrave;nh hay search&nbsp;<a href=\"https://www.w3schools.com/\" target=\"_blank\">https://www.w3schools.com/</a>&nbsp;,&nbsp;<a href=\"https://stackoverflow.com/\" target=\"_blank\">https://stackoverflow.com/</a>&nbsp;, v&agrave; k&egrave;m theo l&agrave; những trang youtube của F8 , Evondev ...</p>\r\n\r\n<p>Sau một khoảng thời gian thực h&agrave;nh nhiều th&igrave; bản th&acirc;n của m&igrave;nh tự nhi&ecirc;n c&oacute; thể code m&agrave; kh&ocirc;ng cần search nhiều v&agrave; gần như l&agrave; m&igrave;nh đ&atilde; nhớ hết c&aacute;c thẻ HTML v&agrave; c&aacute;c thuộc t&iacute;nh CSS thường d&ugrave;ng , cứ nh&igrave;n thấy giao diện l&agrave; code trong đầu của m&igrave;nh tự nhi&ecirc;n hiện l&ecirc;n , cho n&ecirc;n c&aacute;c bạn đừng ngại kh&oacute; cứ thực h&agrave;nh thật nhiều giống m&igrave;nh , c&aacute;c bạn c&oacute; thể t&igrave;m kiếm những landing page đơn giản tr&ecirc;n Figma, Pinterest, Dribble để thực h&agrave;nh.</p>\r\n\r\n<p>Bắt v&agrave;o năm thứ 3 th&igrave; m&igrave;nh đ&atilde; c&oacute; thể kiếm được tiền từ việc code outsource những dự website b&aacute;n h&agrave;ng , blog, tin tức ..., v&agrave; bản th&acirc;n m&igrave;nh cảm thấy rất vui khi c&oacute; thể kiếm được tiền từ những kiến thức m&igrave;nh tự học được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://i.ibb.co/CBb5HYq/A-nh-chu-p-ma-n-hi-nh-49.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; t&oacute;m tắt to&agrave;n bộ qu&aacute; tr&igrave;nh m&agrave; m&igrave;nh tự học Frontend trong v&ograve;ng 1 năm qua v&agrave; m&igrave;nh hi vọng b&agrave;i viết n&agrave;y sẽ tiếp th&ecirc;m động lực cho c&aacute;c bạn mới bước ch&acirc;n v&agrave;o học lập tr&igrave;nh web.</p>', '../uploads/post/11-11-2022/tuyen-dung-nhan-vien-cat-html-css-4400.jpg', 4, '2022-11-11 16:40:40', '2022-11-13 13:12:43', 1, 1),
(2, 'HƯỚNG DẪN FIX LỖI XAMPP MYSQL', '<p>Những c&aacute;ch phổ biến tr&ecirc;n mạng l&agrave; x&oacute;a file ibdata1 trong mysql nhưng c&aacute;ch n&agrave;y rất rủi ro c&oacute; thể th&agrave;nh c&ocirc;ng v&agrave; cũng c&oacute; thể mất hết dữ liệu, sau đ&acirc;y l&agrave; c&aacute;ch gi&uacute;p bạn c&oacute; thể khắc phục được t&igrave;nh trạng lỗi</p>\r\n\r\n<ul>\r\n	<li>V&agrave;o thư mục <strong>xampp </strong>(ổ c)</li>\r\n	<li>T&igrave;m v&agrave; chọn thư mục <strong>mysql</strong></li>\r\n	<li>T&igrave;m thư mục <strong>data</strong> v&agrave; đổi t&ecirc;n th&agrave;nh <strong>data_old </strong></li>\r\n	<li>Tiếp theo l&agrave; copy tất cả file từ <strong>data_old</strong> sang <strong>data</strong> (trừ 3 file mysql, perfomance_schema, phpmyadmin )</li>\r\n	<li>Sau đ&oacute; t&igrave;m thư mục <strong>backup </strong>copy 3 file mysql, perfomance_schema, phpmyadmin v&agrave;o thư mục <strong>data</strong></li>\r\n	<li>V&agrave; khởi động mysql từ xampp</li>\r\n</ul>\r\n\r\n<blockquote>\r\n<p>hello moi nguoi</p>\r\n</blockquote>', '../uploads/post/11-11-2022/Ảnh chụp màn hình (57).png', 5, '2022-11-11 16:50:25', '2022-11-14 02:59:24', 1, 1),
(3, 'NHỮNG MÓN ĐỒ NÊN CÓ TRÊN BÀN LÀM VIỆC', '<h2>1. Biết c&aacute;ch trang tr&iacute; b&agrave;n học c&oacute; lợi &iacute;ch g&igrave;?</h2>\r\n\r\n<h3>1.1. Biết c&aacute;ch trang tr&iacute; b&agrave;n học gi&uacute;p tạo cảm hứng trong học tập</h3>\r\n\r\n<p>B&agrave;n học l&agrave; người bạn đồng h&agrave;nh với ch&uacute;ng ta một khoảng thời gian rất d&agrave;i từ những năm th&aacute;ng tiểu học đến thười THPT, đại học v&agrave; c&oacute; thể c&ograve;n d&agrave;i hơn nữa. Thật vậy ch&uacute;ng ta kh&ocirc;ng n&ecirc;n bỏ qu&ecirc;n g&oacute;c học tập n&agrave;y. Những nguồn cảm hứng &yacute; tưởng mới s&aacute;ng tạo đều bắt nguồn từ kh&ocirc;ng gian m&agrave; ch&uacute;ng ta học tập mỗi ng&agrave;y, sở hữu một chiếc b&agrave;n xinh xắn v&agrave; g&oacute;c học tập gọn g&agrave;ng ngăn nắp th&igrave; gi&uacute;p ch&uacute;ng ta tập trung học tập v&agrave; đạt hiệu tốt nhất mang về những th&agrave;nh t&iacute;ch m&agrave; ch&uacute;ng ta mong đợi. Do đ&oacute; đừng n&ecirc;n chần chừ để trang tr&iacute; b&agrave;n học của m&igrave;nh đ&uacute;ng c&aacute;ch nh&eacute;.</p>\r\n\r\n<h3>1.2 Gi&uacute;p kh&ocirc;ng gian học tập gọn g&agrave;ng v&agrave; ngăn nắp hơn</h3>\r\n\r\n<p>Kiểu d&aacute;ng của sản phẩm c&oacute; thể thay đổi t&ugrave;y v&agrave;o k&iacute;ch thước v&agrave; thiết kế của nh&agrave; sản xuất, tuy nhi&ecirc;n d&ugrave; cho k&iacute;ch thước sản phẩm c&oacute; to hay rộng như thế n&agrave;o m&agrave; ch&uacute;ng ta kh&ocirc;ng biết c&aacute;ch b&agrave;y tr&iacute; th&igrave; g&oacute;c học tập ấy cũng bừa bộn v&agrave; kh&ocirc;ng ngăn nắp. Để tận dụng được triệt để diện t&iacute;ch ch&uacute;ng ta n&ecirc;n chọn những c&aacute;ch b&agrave;y tr&iacute; b&agrave;n học tối ưu nhất điều n&agrave;y vừa khiến ch&uacute;ng ta r&egrave;n luyện t&iacute;nh cẩn thận, chu đ&aacute;o đồng thời việc t&igrave;m kiếm t&agrave;i liệu v&agrave; l&agrave;m b&agrave;i tập được hiệu quả hơn.</p>\r\n\r\n<h3>1.3 Nhắc nhở bản th&acirc;n tập trung với mục ti&ecirc;u đề ra</h3>\r\n\r\n<p>Khi ch&uacute;ng ta ngồi v&agrave;o b&agrave;n học hay l&agrave;m việc nh&igrave;n l&ecirc;n những mục ti&ecirc;u đ&atilde; viết sẵn tr&ecirc;n chiếc b&agrave;n ngăn nắp ch&uacute;ng ta dễ nhận thấy v&agrave; tự nhắc bản th&acirc;n cần n&ecirc;n l&agrave;m những g&igrave; kh&ocirc;ng bị sao nh&atilde;ng, nhiều bạn viết mục ti&ecirc;u học tập của m&igrave;nh ra rồi lại để qu&ecirc;n đ&acirc;u mất hay kẹp ở trong cuốn s&aacute;ch n&agrave;o đ&oacute; m&agrave; kh&ocirc;ng nhớ th&igrave; l&uacute;c ấy ch&uacute;ng ta bị nản v&agrave; mất phương hướng học tập. Hơn thế nữa khi c&oacute; mục ti&ecirc;u r&otilde; rang th&igrave; những yếu tố ngoại cảnh ( tiếng nhạc, tivi, &acirc;m thanh người n&oacute;i chuyện,&hellip;) sẽ &iacute;t ảnh hưởng với ch&uacute;ng ta hơn bởi sự tập trung v&agrave;o ti&ecirc;u gi&uacute;p ta c&oacute; định hướng r&otilde; r&agrave;ng. Trang tr&iacute; b&agrave;n học gọn g&agrave;ng đẹp mắt, để ch&uacute;ng ta thấy được lời nhắc m&agrave; mục ti&ecirc;u đ&atilde; đề ra cần phải l&agrave;m g&igrave; để đạt được mục ti&ecirc;u đ&oacute;.</p>\r\n\r\n<h3>1.4 Thể hiện được gu thẩm mỹ v&agrave; c&aacute; t&iacute;nh mỗi người</h3>\r\n\r\n<p>C&aacute;ch trang tr&iacute; b&agrave;n học c&oacute; rất nhiều c&aacute;ch nhưng với mỗi người th&igrave; lại c&oacute; c&aacute;ch trang tr&iacute; rất ri&ecirc;ng biệt bởi đ&oacute; l&agrave; thể hiện sở th&iacute;ch v&agrave; t&iacute;nh c&aacute;ch ri&ecirc;ng của mỗi c&aacute; nh&acirc;n ch&uacute;ng ta, điển h&igrave;nh như con trai th&igrave; th&iacute;ch trang tr&iacute; những gam m&agrave;u mạnh mẽ như m&agrave;u xanh c&ograve;n con g&aacute;i y&ecirc;u th&iacute;ch sự nhẹ nh&agrave;ng th&igrave; chọn m&agrave;u hồng. C&oacute; những bạn tận dụng yếu tố về m&agrave;u sắc để trang tr&iacute; cho b&agrave;n học hay l&agrave;m việc hợp với phong thủy v&agrave; hợp mệnh của m&igrave;nh để từ đ&oacute; m&agrave; c&ocirc;ng việc hay học h&agrave;nh đạt được những th&agrave;nh t&iacute;ch cao hơn v&agrave; thuận lợi hơn.</p>\r\n\r\n<h2>2. Gợi &yacute; những m&oacute;n đồ cần c&oacute; tr&ecirc;n b&agrave;n học v&agrave; l&agrave;m việc</h2>\r\n\r\n<h3>2.1 M&aacute;y phun tinh dầu</h3>\r\n\r\n<p><img src=\"https://i.ibb.co/9HWJPsw/PXL-20220917-111938883-NIGHT.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lợi &iacute;ch khi sử dụng m&aacute;y x&ocirc;ng tinh dầu tạo độ ẩm:</strong></p>\r\n\r\n<ul>\r\n	<li>Gi&uacute;p giấc ngủ ngon v&agrave; s&acirc;u hơn.</li>\r\n	<li>Giảm cảm lạnh v&agrave; cảm c&uacute;m.</li>\r\n	<li>L&agrave;m sạch kh&ocirc;ng kh&iacute;, loại bỏ 1 số vi khuẩn, vi r&uacute;t g&acirc;y bệnh.</li>\r\n	<li>Hỗ trợ hệ h&ocirc; hấp.</li>\r\n	<li>Giảm căng thẳng mệt mỏi, stress.</li>\r\n	<li>Giảm c&ocirc;n tr&ugrave;ng, đuổi muỗi hiệu quả.</li>\r\n	<li>Giảm đau.</li>\r\n	<li>Khử m&ugrave;i hiệu quả.</li>\r\n	<li>Khuếch t&aacute;n hương tinh dầu nhanh v&agrave; mạnh hơn.</li>\r\n	<li>C&oacute; thể mua sản phẩm tại đ&acirc;y :&nbsp;<a href=\"https://www.facebook.com/vitrannnn/\" target=\"_blank\">https://www.facebook.com/vitrannnn/</a></li>\r\n</ul>\r\n\r\n<h3>2.2 Một chiếc loa mini</h3>\r\n\r\n<ul>\r\n	<li>Gi&uacute;p bạn c&oacute; thể nghe nhạc lofi cực chill trong l&uacute;c l&agrave;m việc để giảm căng thẳng</li>\r\n	<li>Gi&uacute;p bạn c&oacute; được &acirc;m thanh tốt hơn trong những lần học online v&agrave; những cuộc hợp ...</li>\r\n</ul>\r\n\r\n<h3>2.3 Đồng hồ l&agrave;m bằng gỗ</h3>\r\n\r\n<p><img src=\"https://i.ibb.co/9YGCP3n/1-3-dong-ho-go-de-ban-lam-viec.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Việc sử dụng những chiếc đồng hồ bằng gỗ đang được nhiều người ưu chuộng bởi sự đơn giản v&agrave; tinh tế m&agrave; n&oacute; mang lại. Đồng hồ hiển thị những con số tr&ecirc;n nền gỗ mang lại n&eacute;t độc đ&aacute;o, hiện đại cho g&oacute;c l&agrave;m việc của bạn.</li>\r\n</ul>\r\n\r\n<h3>2.4 C&acirc;y may mắn</h3>\r\n\r\n<p><img src=\"https://i.ibb.co/3Tchs3X/2-2-cay-may-man-trang-tri.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>C&acirc;y may mắn được xem l&agrave; c&acirc;y phong thủy th&acirc;n thiện với người chơi c&acirc;y cảnh. Ch&uacute;ng kh&aacute; dễ chăm s&oacute;c, kh&ocirc;ng qu&aacute; cầu k&igrave; v&agrave; thường được đặt tr&ecirc;n b&agrave;n l&agrave;m việc gần m&aacute;y t&iacute;nh của bạn để loại bỏ năng lượng điện từ ph&aacute;t ra trong qu&aacute; tr&igrave;nh sử dụng, giảm căng thẳng trong c&ocirc;ng việc cũng như tạo được c&acirc;y cảnh trong văn ph&ograve;ng.C&acirc;y may mắn giữ vai tr&ograve; như bộ lọc năng lượng đẩy l&ugrave;i tất cả năng lượng ti&ecirc;u cực, thu h&uacute;t năng lượng t&iacute;ch cực v&agrave; điều h&ograve;a sự lưu th&ocirc;ng của năng lượng.</li>\r\n</ul>\r\n\r\n<h3>2.5 Đ&egrave;n trang tr&iacute; l&agrave;m việc</h3>\r\n\r\n<p><img src=\"https://i.ibb.co/qB54xXd/4-1-den-trang-tri-ban-lam-viec.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Một g&oacute;c l&agrave;m việc đẹp th&igrave; kh&ocirc;ng thể n&agrave;o thiếu đi chiếc đ&egrave;n chiếu s&aacute;ng. Ngo&agrave;i việc cung cấp đầy đủ nguồn s&aacute;ng, tr&aacute;nh bị ảnh xấu tới thị gi&aacute;c, sức khỏe th&igrave; chiếc đ&egrave;n ng&agrave;y nay c&ograve;n được thiết kế đẹp mắt, kiểu d&aacute;ng độc đ&aacute;o mới lại, tạo n&ecirc;n sức h&uacute;t hơn cho g&oacute;c l&agrave;m việc. Bạn ho&agrave;n to&agrave;n c&oacute; thể t&igrave;m thấy rất nhiều mẫu đ&egrave;n trang tr&iacute; để b&agrave;n gi&uacute;p t&ocirc; điểm g&oacute;c l&agrave;m việc th&ecirc;m cuốn h&uacute;t hơn.</li>\r\n</ul>', '../uploads/post/13-11-2022/photo-1623924174319-eddb1f66ae5e.jpg', 6, '2022-11-13 02:41:26', '2022-11-13 12:46:21', 0, 1),
(4, 'Leica ra mắt smartphone với camera “khủng”, giá bằng Galaxy Z Fold4', '<p>Mới đ&acirc;y, thương hiệu chuy&ecirc;n về nhiếp ảnh Leica đ&atilde; giới thiệu mẫu smartphone mới nhất của h&atilde;ng l&agrave;&nbsp;<strong>Leitz Phone 2</strong>. Mẫu m&aacute;y nổi bật với cụm camera với cảm biến si&ecirc;u lớn, thiết kế độc lạ v&agrave; mức gi&aacute; bằng một chiếc Galaxy Z Fold4.</p>\r\n\r\n<p><img alt=\"Hình ảnh demo 1\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Leica-Leitz-Phone-2.jpg\" style=\"width:960px\" /></p>\r\n\r\n<p>Về thiết kế, Leitz Phone 2 lấy cảm hứng từ những mẫu m&aacute;y Sharp Aquos, với bốn g&oacute;c m&aacute;y v&aacute;t vu&ocirc;ng đi k&egrave;m c&aacute;c gờ ở khung viền. Phần mặt lưng nổi bật với cụm camera được thiết kế dưới dạng h&igrave;nh tr&ograve;n, tương đối giống Xiaomi 12S Ultra hay Nokia Lumia 1020 trước đ&oacute;. Ph&iacute;a b&ecirc;n trong chứa camera ch&iacute;nh với độ ph&acirc;n giải l&ecirc;n tới 47,2MP, cảm biến ảnh 1 inch v&agrave; khẩu độ f/1.9. Ngo&agrave;i ra, h&atilde;ng cũng trang bị th&ecirc;m tr&ecirc;n Leitz Phone 2 một camera hỗ trợ chụp ảnh ch&acirc;n dung x&oacute;a ph&ocirc;ng, với độ ph&acirc;n giải chỉ 1,9MP.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Leica-Leitz-Phone-3.jpg\" style=\"width:960px\" /></p>\r\n\r\n<p>C&aacute;c th&ocirc;ng số kỹ thuật kh&aacute;c tr&ecirc;n Leitz Phone 2 cũng thuộc ph&acirc;n kh&uacute;c &ldquo;cao cấp&rdquo; với chipset Snapdragon 8 Gen 1, 12GB RAM v&agrave; 512GB ROM. Ở mặt trước, m&aacute;y được trang bị m&agrave;n h&igrave;nh lớn 6,6 inch, độ ph&acirc;n giải 2.730 x 1.260 pixels, v&agrave; đặc biệt c&oacute; tần số qu&eacute;t l&ecirc;n tới 240Hz. Vi&ecirc;n pin được trang bị tr&ecirc;n Leitz Phone 2 c&oacute; dung lượng 5.000mAh, v&agrave; m&aacute;y sẽ được c&agrave;i đặt sẵn Android 12 ngay khi xuất xưởng.</p>\r\n\r\n<p>Leica Leitz Phone 2 được b&aacute;n ra với mức gi&aacute; ni&ecirc;m yết hơn 225.000&yen; (khoảng 40 triệu đồng). Mức gi&aacute; n&agrave;y được cho l&agrave; kh&ocirc;ng hề rẻ, ngang bằng với Galaxy Z Fold4 v&agrave; đắt hơn hầu hết c&aacute;c smartphone đang được kinh doanh hiện tại. Người d&ugrave;ng c&oacute; thể đặt trước mẫu m&aacute;y kể từ ng&agrave;y 18/11 sắp tới.</p>', '../uploads/post/13-11-2022/Leica-Leitz-Phone-1.jpg', 7, '2022-11-13 13:27:59', '2022-11-14 02:35:13', 1, 1),
(5, 'Ứng dụng iCloud Photo đã có mặt trên Windows 11, người dùng iPhone nên dùng thử', '<p>H&ocirc;m nay, Microsoft đ&atilde; ch&iacute;nh thức th&ocirc;ng b&aacute;o rằng, t&iacute;nh năng t&iacute;ch hợp Ảnh iCloud trong ứng dụng Ảnh tr&ecirc;n Windows 11 đ&atilde; được ph&aacute;t h&agrave;nh với bản cập nhật cho ứng dụng tr&ecirc;n Microsoft Store. Microsoft hy vọng, bản cập nhật sẽ c&oacute; sẵn cho tất cả kh&aacute;ch h&agrave;ng sử dụng Windows 11 v&agrave;o cuối th&aacute;ng n&agrave;y.</p>\r\n\r\n<p>T&iacute;ch hợp Ảnh iCloud cho ph&eacute;p người d&ugrave;ng iPhone truy cập tất cả ảnh của họ tr&ecirc;n c&aacute;c thiết bị chạy Windows 11. Trong một b&agrave;i đăng tr&ecirc;n blog, Microsoft cho biết, người d&ugrave;ng chỉ cần tải xuống ứng dụng iCloud cho Windows từ Microsoft Store, bật đồng bộ h&oacute;a Ảnh iCloud v&agrave; ảnh sẽ tự động xuất hiện trong ứng dụng Ảnh đ&atilde; cập nhật.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Apple-icloud-photo-tren-windows-11.jpg\" style=\"width:960px\" /></p>\r\n\r\n<p>Microsoft trước đ&oacute; đ&atilde; th&ocirc;ng b&aacute;o rằng, c&aacute;c ứng dụng Apple Music v&agrave; Apple TV sẽ đến với c&aacute;c thiết bị chạy Windows v&agrave;o năm tới. Hiện tại, người d&ugrave;ng Windows phải truy cập dịch vụ ph&aacute;t trực tuyến Apple TV+ tr&ecirc;n web tại tv.apple.com, trong khi Apple Music c&oacute; sẵn tr&ecirc;n web hoặc trong ứng dụng iTunes d&agrave;nh cho c&aacute;c bản Windows cũ. Microsoft cho biết, bản xem trước của cả hai ứng dụng sẽ c&oacute; sẵn tr&ecirc;n Microsoft Store bắt đầu từ cuối năm nay.</p>\r\n\r\n<p>Trước đ&oacute;, Apple đ&atilde; th&ocirc;ng b&aacute;o về việc tăng gi&aacute; c&aacute;c g&oacute;i dịch vụ, bao gồm: Apple Music, Apple TV + v&agrave; Apple One. Đ&aacute;ng ch&uacute; &yacute;, đ&acirc;y l&agrave; lần đầu ti&ecirc;n Apple thực hiện việc tăng gi&aacute; c&aacute;c g&oacute;i dịch vụ n&agrave;y. Cụ thể hơn, dưới đ&acirc;y sẽ l&agrave; bảng gi&aacute; ni&ecirc;m yết mới c&aacute;c g&oacute;i dịch vụ tương ứng của Apple tại thị trường Việt Nam:</p>\r\n\r\n<p><strong>Apple Music</strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>G&oacute;i Apple Music</strong></td>\r\n			<td><strong>Gi&aacute; cũ</strong></td>\r\n			<td><strong>Gi&aacute; mới</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Student</td>\r\n			<td>29.000đ / 1 th&aacute;ng</td>\r\n			<td>35.000đ / 1 th&aacute;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Individual</td>\r\n			<td>59.000đ / 1 th&aacute;ng</td>\r\n			<td>65.000đ / 1 th&aacute;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Family</td>\r\n			<td>89.000đ / 1 th&aacute;ng</td>\r\n			<td>99.000đ / 1 th&aacute;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Tại Việt Nam, Apple kh&ocirc;ng cung cấp g&oacute;i c&aacute; nh&acirc;n h&agrave;ng năm. Mức gi&aacute; ni&ecirc;m yết mới m&agrave; Apple c&ocirc;ng bố tăng từ 1-2$. Tuy nhi&ecirc;n, tại Việt Nam, gi&aacute; cho mỗi g&oacute;i Apple Music chỉ điều chỉnh nhẹ</em></p>\r\n\r\n<p><strong>G&oacute;i</strong>&nbsp;<strong>Apple TV+</strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Apple TV+</td>\r\n			<td>Gi&aacute; cũ</td>\r\n			<td>Gi&aacute; mới</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Monthly</td>\r\n			<td>115.000đ /1 th&aacute;ng</td>\r\n			<td>179.000đ /1 th&aacute;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Gi&aacute; Apple TV+ tăng mạnh l&ecirc;n đến 65.000đ</em></p>\r\n\r\n<p><strong>Apple One</strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><strong>G&oacute;i Apple One</strong></td>\r\n			<td>Gi&aacute; cũ</td>\r\n			<td>Gi&aacute; mố</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Individual</td>\r\n			<td>175.000đ /1 th&aacute;ng</td>\r\n			<td>219.000đ /1 th&aacute;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Family</td>\r\n			<td>199.000đ / 1 th&aacute;ng</td>\r\n			<td>249.000đ / 1 th&aacute;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Với Apple One, gi&aacute; tăng nhẹ cho cho cả 2 g&oacute;i l&agrave; 44.000đ v&agrave; 50.000đ</em></p>\r\n\r\n<ul>\r\n	<li>Với g&oacute;i Individual (C&aacute; nh&acirc;n): Người d&ugrave;ng c&oacute; thể sử dụng c&aacute;c dịch vụ bao gồm Apple Music, Apple TV +, Apple Arcade v&agrave; 50GB dung lượng lưu trữ iCloud.</li>\r\n	<li>Với g&oacute;i Family (Gia đ&igrave;nh): Người d&ugrave;ng sẽ được sử dụng những dịch vụ giống với g&oacute;i C&aacute; nh&acirc;n, tuy nhi&ecirc;n dung lượng iCloud sẽ được tăng l&ecirc;n l&agrave; 200GB v&agrave; được chia sẻ cho tối đa 5 th&agrave;nh vi&ecirc;n kh&aacute;c c&ugrave;ng sử dụng.</li>\r\n</ul>\r\n\r\n<p>L&yacute; giải về việc tăng gi&aacute;, Apple cho biết rằng do c&oacute; một số chi ph&iacute; li&ecirc;n quan đến bản quyền đ&atilde; tăng l&ecirc;n, do đ&oacute; họ bắt buộc phải n&acirc;ng gi&aacute; dịch vụ. Đối với Apple Music, việc tăng gi&aacute; l&agrave; để trả th&ecirc;m một khoản hoa hồng cho c&aacute;c nghệ sĩ. C&ograve;n với Apple TV+ l&agrave; do dịch vụ n&agrave;y đ&atilde; cung cấp th&ecirc;m nhiều nội dung hơn. Với iCloud, hiện chưa c&oacute; bất kỳ biến động n&agrave;o về gi&aacute; cả. Những người đ&atilde; đăng k&yacute; &zwnj;Apple Music&zwnj;, &zwnj;Apple TV + v&agrave; &zwnj;Apple One&zwnj; sẽ nhận được th&ocirc;ng b&aacute;o về việc tăng gi&aacute; trước 30 ng&agrave;y kể từ khi th&ocirc;ng b&aacute;o hiệu lực, do đ&oacute; họ c&oacute; thời gian để hủy nếu kh&ocirc;ng muốn tiếp tục sử dụng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><!--?php echo hello ?--></p>\r\n\r\n<p>&nbsp;</p>', '../uploads/post/13-11-2022/Apple-icloud-photo-tren-windows-11.jpg', 7, '2022-11-13 13:30:45', '2022-11-14 07:00:16', 1, 1),
(6, 'Apple phát hành iOS 16.2 Public Beta 2 với Ứng dụng Freeform hoàn toàn mới', '<p>Một tuần sau khi ph&aacute;t h&agrave;nh bản iOS 16.2 public beta đầu ti&ecirc;n, Apple đ&atilde; ph&aacute;t h&agrave;nh bản iOS 16.2 public beta thứ 2 cho người d&ugrave;ng trải nghiệm v&agrave; c&aacute;c nh&agrave; ph&aacute;t triển. Người d&ugrave;ng thử nghiệm c&aacute;c bản beta c&ocirc;ng khai đ&atilde; đăng k&yacute; trải nghiệm c&oacute; thể tải xuống tr&ecirc;n c&aacute;c đời iPhone v&agrave; iPad hỗ trợ.</p>\r\n\r\n<p>Trong đ&oacute;, iOS 16.2 v&agrave; iPadOS 16.2 giới thiệu&nbsp;<a href=\"https://vatvostudio.vn/freeform-ung-dung-hoan-toan-moi-tren-iphone-ipad-macbook-co-gi-hay/\">Freeform</a>&nbsp;&ndash; ứng dụng c&oacute; thể được sử dụng để ghi ch&uacute;, ph&aacute;c thảo &yacute; tưởng, viết vẽ v&agrave; hơn thế nữa. Điểm hay của ứng dụng n&agrave;y đ&oacute; ch&iacute;nh l&agrave; việc n&oacute; hoạt động theo thời gian thực, tức l&agrave; khi người d&ugrave;ng kh&aacute;c tương t&aacute;c, bạn cũng sẽ c&oacute; thể nh&igrave;n thấy được sự tương t&aacute;c đ&oacute; ngay lập tức. Khả năng n&agrave;y sẽ gi&uacute;p cho những người d&ugrave;ng c&oacute; thể bổ trợ cho những &yacute; tưởng ngay lập tức, l&agrave;m cho qu&aacute; tr&igrave;nh cộng t&aacute;c trở n&ecirc;n ch&acirc;n thực hơn, gần gũi hơn.</p>\r\n\r\n<p>Cũng như những ứng dụng ghi ch&uacute;, người d&ugrave;ng được cung cấp một loạt c&aacute;c c&ocirc;ng cụ d&agrave;nh cho việc viết, vẽ v&agrave; đ&aacute;nh dấu bao gồm b&uacute;t ch&igrave;, b&uacute;t mực, b&uacute;t dạ v&agrave; đổ mực. R&otilde; r&agrave;ng, loại ứng dụng như vậy n&oacute;i chung v&agrave; Freeform n&oacute;i ri&ecirc;ng sẽ hoạt động tối hiệu quả tr&ecirc;n iPad bởi ch&uacute;ng c&oacute; hỗ trợ Apple Pencil, gi&uacute;p tối ưu cho qu&aacute; tr&igrave;nh ghi ch&uacute;, tương t&aacute;c.</p>\r\n\r\n<p>Đối với iPad, bản cập nhật mang lại khả năng hỗ trợ hiển thị b&ecirc;n ngo&agrave;i cho t&iacute;nh năng đa nhiệm Stage Manager, cho ph&eacute;p sử dụng tối đa 8 ứng dụng c&ugrave;ng một l&uacute;c. Trong đ&oacute;, Hỗ trợ m&agrave;n h&igrave;nh ngo&agrave;i c&oacute; sẵn cho c&aacute;c mẫu iPad M1 v&agrave; iPad M2.</p>\r\n\r\n<p>Th&ecirc;m nữa, c&oacute; một giao diện ứng dụng Home mới xuất hiện trong iOS 16.2 v&agrave; iPadOS 16.2, m&agrave; Apple cho biết được thiết kế để mang lại sự tiện lợi hơn, đ&aacute;ng tin cậy hơn, đặc biệt l&agrave; trong những ng&ocirc;i nh&agrave; c&oacute; nhiều phụ kiện nh&agrave; th&ocirc;ng minh. Giao diện mới y&ecirc;u cầu phần mềm HomePod 16.2 beta.</p>', '../uploads/post/13-11-2022/iOS-16.2-PUBLIC-BETA-2-UNG-DUNG-FREEFORM.jpg', 7, '2022-11-13 15:43:43', '2022-11-14 03:19:45', 0, 1),
(7, 'Văn Hậu bí mật chuyện xuất ngoại sau ngôi vô địch V.League', '<h3>GD&amp;TĐ - Đo&agrave;n Văn Hậu chưa muốn tiết lộ kế hoạch xuất ngoại sau ng&ocirc;i v&ocirc; địch V.League c&ugrave;ng H&agrave; Nội FC.</h3>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Văn Hậu chưa muốn bàn chuyện xuất ngoại sau ngôi vô địch V.League.\" src=\"https://photo-cms-giaoducthoidai.epicdn.me/w820/Uploaded/2022/ygtmrk/2022_11_13/-8900.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">Văn Hậu chưa muốn b&agrave;n chuyện xuất ngoại sau ng&ocirc;i v&ocirc; địch V.League.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>V&ograve;ng &aacute;p ch&oacute;t V-League vừa kết th&uacute;c. Theo đ&oacute;, S&agrave;i G&ograve;n FC hay H&agrave; Tĩnh xuống hạng phải chờ kết th&uacute;c lượt trận cuối c&ugrave;ng v&agrave;o ng&agrave;y 19/11.</p>\r\n\r\n<p>Sau v&ograve;ng 25 V-League, S&agrave;i G&ograve;n vẫn ở vị tr&iacute; đ&aacute;y bảng với 19 điểm, c&ograve;n H&agrave; Tĩnh c&oacute; 21 điểm v&agrave; &iacute;t nhiều chiếm lợi thế ở cuộc đua trụ hạng.</p>\r\n\r\n<p>Trận thắng 3-0 trước S&agrave;i G&ograve;n FC tr&ecirc;n s&acirc;n Thi&ecirc;n Trường gi&uacute;p đội b&oacute;ng th&agrave;nh Nam ch&iacute;nh thức trụ hạng sớm một v&ograve;ng đấu v&agrave; ẵm số tiền thưởng lớn từ ban l&atilde;nh đạo.</p>\r\n\r\n<p>Trong khi đ&oacute;, H&agrave; Nội đ&aacute;nh bại H&agrave; Tĩnh 2-0 tr&ecirc;n s&acirc;n H&agrave;ng Đẫy, ch&iacute;nh thức l&ecirc;n ng&ocirc;i V-League 2022 với 50 điểm. CLB Hải Ph&ograve;ng xếp nh&igrave; với 48 điểm v&agrave; đ&atilde; kết th&uacute;c h&agrave;nh tr&igrave;nh ở V.League năm nay trong khi đội b&oacute;ng Thủ đ&ocirc; c&ograve;n một trận chưa đấu.</p>\r\n\r\n<p>Sau khi c&ugrave;ng CLB H&agrave; Nội l&ecirc;n ng&ocirc;i v&ocirc; địch ở m&ugrave;a giải 2022, hậu vệ Đo&agrave;n Văn Hậu chia sẻ với giới truyền th&ocirc;ng: &ldquo;Về chuyện xuất ngoại, t&ocirc;i sẽ kh&ocirc;ng tiết lộ g&igrave; qu&aacute; nhiều m&agrave; giữ b&iacute; mật cho ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p>M&ugrave;a n&agrave;y, t&ocirc;i chỉ mới trở lại sau chấn thương v&agrave; thực sự rất vui khi c&oacute; thể đ&oacute;ng g&oacute;p cho CLB. T&ocirc;i hy vọng trong phần c&ograve;n lại của năm 2022 m&igrave;nh sẽ trau dồi, tập luyện, t&iacute;ch lũy kinh nghiệm v&agrave; thi đấu tốt hơn nữa&rdquo;.</p>\r\n\r\n<p>Đo&agrave;n Văn Hậu cũng tiết lộ muốn tập trung v&agrave;o đấu trường AFC Champions League, nơi H&agrave; Nội FC sẽ đại diện cho Việt Nam tranh t&agrave;i: &ldquo;T&ocirc;i nghĩ mục ti&ecirc;u ch&iacute;nh vẫn l&agrave; chiến đấu hết sức v&igrave; m&agrave;u cờ sắc &aacute;o, v&igrave; CLB, v&igrave; danh hiệu.</p>\r\n\r\n<p>Những thứ c&ograve;n lại kh&ocirc;ng quan trọng. Mặt kh&aacute;c, việc được g&oacute;p mặt ở đấu trường ch&acirc;u lục l&agrave; mơ ước của c&aacute;c cầu thủ. Đ&oacute; l&agrave; s&acirc;n chơi lớn, gi&uacute;p ch&uacute;ng t&ocirc;i ph&aacute;t triển. T&ocirc;i rất hồi hộp v&agrave; hy vọng l&agrave; ng&agrave;y được ra s&acirc;n tại AFC Champions League sẽ đến nhanh hơn nữa&rdquo;.</p>\r\n\r\n<p>Cuối c&ugrave;ng, Đo&agrave;n Văn Hậu khẳng định mục ti&ecirc;u trong phần c&ograve;n lại của năm 2022 l&agrave; v&agrave;o chung kết C&uacute;p Quốc gia v&agrave; đạt th&agrave;nh t&iacute;ch tốt nhất c&oacute; thể tại AFF Cup.</p>', '../uploads/post/14-11-2022/-8900.jpg', 8, '2022-11-14 04:19:16', '2022-11-14 04:19:16', 0, 1),
(8, 'Google Pay chính thức hoạt động tại Việt Nam, mời anh em cài đặt và trải nghiệm', '<p>Sau b&agrave;i viết k&iacute;ch hoạt thủ c&ocirc;ng&nbsp;<a href=\"https://tinhte.vn/tag/google-pay\">Google Pay</a>&nbsp;(GPay) cho những thiết bị di động&nbsp;<a href=\"https://tinhte.vn/tag/android\">Android</a>&nbsp;tại Việt Nam c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u th&igrave; b&acirc;y giờ, t&iacute;nh năng&nbsp;<a href=\"https://tinhte.vn/tag/thanh-toan-khong-cham\">thanh to&aacute;n kh&ocirc;ng chạm</a>&nbsp;cho c&aacute;c thiết bị Android đ&atilde; ch&iacute;nh thức hoạt động tại Việt Nam, d&agrave;nh cho người d&ugrave;ng sở hữu thẻ VISA của một số ng&acirc;n h&agrave;ng trong danh s&aacute;ch dưới đ&acirc;y. Ngay b&acirc;y giờ anh em c&oacute; thể v&agrave;o&nbsp;<a href=\"https://tinhte.vn/tag/google\">Google</a>&nbsp;Play Store (CH Play) để tải v&agrave; c&agrave;i đặt&nbsp;<a href=\"https://tinhte.vn/tag/google-wallet\">Google Wallet</a>&nbsp;m&agrave; kh&ocirc;ng cần phải đổi v&ugrave;ng.</p>\r\n\r\n<p>Danh s&aacute;ch c&aacute;c ng&acirc;n h&agrave;ng hỗ trợ Google Pay (Google Wallet), thẻ VISA Debit hay Credit đều được:</p>\r\n\r\n<ul>\r\n	<li>Ng&acirc;n h&agrave;ng &Aacute; Ch&acirc;u (<a href=\"https://tinhte.vn/tag/acb\">ACB</a>).</li>\r\n	<li>Ng&acirc;n h&agrave;ng&nbsp;<a href=\"https://tinhte.vn/tag/vietcombank\">Vietcombank</a>.</li>\r\n	<li>Ng&acirc;n h&agrave;ng S&agrave;i G&ograve;n Thương T&iacute;n (<a href=\"https://tinhte.vn/tag/sacombank\">Sacombank</a>).</li>\r\n	<li>Ng&acirc;n h&agrave;ng Shinhan Bank.</li>\r\n	<li>Ng&acirc;n h&agrave;ng Ti&ecirc;n Phong (<a href=\"https://tinhte.vn/tag/tpbank\">TPBank</a>).</li>\r\n	<li>Ng&acirc;n h&agrave;ng Việt Nam Thịnh Vượng (<a href=\"https://tinhte.vn/tag/vpbank\">VPBank</a>).</li>\r\n	<li>Ng&acirc;n h&agrave;ng thương mại cổ phần kỹ thương Việt Nam (Techcombank),&nbsp;<strong>ri&ecirc;ng Techcombank chỉ hỗ trợ Credit (thẻ t&iacute;n dụng).</strong></li>\r\n</ul>\r\n\r\n<p><br />\r\nVề trải nghiệm sử dụng v&agrave; thanh to&aacute;n th&igrave; giống như trong b&agrave;i viết n&agrave;y m&igrave;nh c&oacute; chia sẻ, anh em c&agrave;i Google Wallet (trong b&agrave;i trước l&agrave; GPay - anh em đ&atilde; c&agrave;i GPay trước đ&oacute; th&igrave; chỉ cần v&agrave;o cập nhật l&agrave; được) v&agrave; thao t&aacute;c th&ecirc;m thẻ v&agrave;o, sau đ&oacute; c&oacute; thể d&ugrave;ng điện thoại để thanh to&aacute;n kh&ocirc;ng chạm tại c&aacute;c m&aacute;y POS c&oacute; t&iacute;ch hợp t&iacute;nh năng thanh to&aacute;n kh&ocirc;ng chạm (contactless), tất nhi&ecirc;n thẻ VISA của anh em cũng phải l&agrave; loại contactless th&igrave; mới trải nghiệm được GPay. Ngo&agrave;i việc th&ecirc;m thẻ ng&acirc;n h&agrave;ng, anh em c&ograve;n c&oacute; thể th&ecirc;m những loại thẻ kh&aacute;c như thẻ th&agrave;nh vi&ecirc;n (m&igrave;nh th&ecirc;m Starbucks chẳng hạn) hay v&eacute; m&aacute;y bay, thẻ qu&agrave; tặng&hellip;<br />\r\n<img alt=\"GPay.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2022/11/6206924_GPay.jpg\" /><br />\r\n<em>Hiện tại m&igrave;nh đ&atilde; th&ecirc;m được 2 thẻ VISA đang sử dụng v&agrave; một thẻ Starbucks, tuy vậy Starbucks Việt nam chưa hỗ trợ GPay n&ecirc;n kh&ocirc;ng thể th&ecirc;m thẻ một c&aacute;ch ho&agrave;n chỉnh m&agrave; chỉ l&agrave; th&ecirc;m số thẻ th&ocirc;i.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://photo2.tinhte.vn/data/attachment-files/2022/11/6206926_Screenshot_2022-11-15-07-57-13-553_com.google.android.gms.jpg\" /></em></p>\r\n\r\n<p><em>V&iacute; dụ khi th&ecirc;m thẻ VISA của VPBank, Google Wallet sẽ y&ecirc;u cầu nhập m&atilde; x&aacute;c thưc, anh em c&oacute; thể chọn d&ograve;ng dưới để ng&acirc;n h&agrave;ng gửi m&atilde; x&aacute;c thực qua tin nhắn.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"https://photo2.tinhte.vn/data/attachment-files/2022/11/6206930_Screenshot_2022-11-15-07-59-25-823_com.google.android.gms.jpg\" />Th&ecirc;m thẻ th&agrave;nh c&ocirc;ng sẽ b&aacute;o như thế n&agrave;y.</em><br />\r\n<br />\r\nTuy vậy thời gian đầu nhiều nơi sẽ chưa thể biết được về c&aacute;ch thức thanh to&aacute;n n&agrave;y, chưa quen hoặc chưa từng nghe đến. Anh em cần giải th&iacute;ch cho nh&acirc;n vi&ecirc;n thu ng&acirc;n l&agrave; rằng nếu m&aacute;y c&agrave; thẻ của họ c&oacute; hỗ trợ thanh to&aacute;n kh&ocirc;ng chạm th&igrave; c&oacute; thể thanh to&aacute;n với GPay. M&igrave;nh đ&atilde; thử ở Cheese Coffee, Starbucks v&agrave; Katinat th&igrave; cả 3 hệ thống c&agrave; ph&ecirc; n&agrave;y đều c&oacute; thể thanh to&aacute;n với GPay, n&oacute; l&agrave;m thời gian m&igrave;nh từ l&uacute;c v&agrave;o quầy cho đến khi gọi m&oacute;n v&agrave; thanh to&aacute;n xong chỉ mất 20-30 gi&acirc;y m&agrave; th&ocirc;i, vừa nhanh gọn cho m&igrave;nh m&agrave; người ở sau cũng kh&ocirc;ng phải chờ l&acirc;u.<br />\r\n<br />\r\nHiện tại anh em chỉ th&ecirc;m được thẻ VISA n&ecirc;n c&oacute; thể thời gian đầu n&agrave;y chỉ hỗ trợ đối với loại thẻ n&agrave;y m&agrave; th&ocirc;i, nhưng trong tương lai c&oacute; thể sẽ hỗ trợ th&ecirc;m. V&igrave; vậy cũng trong b&agrave;i n&agrave;y m&igrave;nh cũng k&ecirc;u gọi anh em h&atilde;y thử add thẻ ng&acirc;n h&agrave;ng m&agrave; anh em đang c&oacute; với điện thoại m&agrave; anh em đang sử dụng, sau đ&oacute; comment lại trong b&agrave;i n&agrave;y để c&aacute;c anh em kh&aacute;c nếu c&ugrave;ng sử dụng sẽ c&oacute; th&ecirc;m sự tham khảo nh&eacute;. Chi tiết hơn anh em c&oacute; thể tham khảo th&ecirc;m t&agrave;i liệu của Google.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><a href=\"https://support.google.com/wallet/answer/12315406\" rel=\"nofollow\" target=\"_blank\">Vietnam: Supported payment methods - Google Wallet Help</a></h4>\r\n\r\n<p><a href=\"https://support.google.com/wallet/answer/12315406\" rel=\"nofollow\" target=\"_blank\">To find out if your bank or card supports contactless payments with Google Wallet, check the table below. For other countries and regions, find supported payment methods for tap to pay transactions.</a></p>\r\n\r\n<p><a href=\"https://support.google.com/wallet/answer/12315406\" rel=\"nofollow\" target=\"_blank\">&nbsp;support.google.com</a></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>UPDATE</strong>: Anh em d&ugrave;ng iOS vẫn c&oacute; thể sử dụng được Google Pay nh&eacute;, vẫn tải v&agrave; c&agrave;i đặt b&igrave;nh thường, chỉ l&agrave; kh&ocirc;ng thanh to&aacute;n tap &amp; pay được th&ocirc;i. Hơn nữa, mới chỉ hỗ trợ 3 quốc gia l&agrave; Mỹ, Singapore v&agrave; Ấn Độ. Link tải dưới đ&acirc;y cho anh em n&agrave;o đang ở 3 quốc gia tr&ecirc;n:<br />\r\n&nbsp;</p>\r\n\r\n<p><a href=\"https://apps.apple.com/us/app/google-pay-save-and-pay/id1193357041\" rel=\"nofollow\" target=\"_blank\"><img src=\"https://imgproxy4.tinhte.vn/Gjr1XJnUQzU4meba1fjJT5fRgDGO4WkF6B1kJA-WxMg/rs:fill:480:300:0/plain/https://is3-ssl.mzstatic.com/image/thumb/Purple112/v4/5f/bd/8b/5fbd8b7d-dcd6-cc13-b096-453c7dd058a8/GPayAppIcon-0-1x_U007emarketing-0-0-0-5-0-0-85-220.png/1200x630wa.png\" style=\"height:300px; width:480px\" /></a></p>\r\n\r\n<h4><a href=\"https://apps.apple.com/us/app/google-pay-save-and-pay/id1193357041\" rel=\"nofollow\" target=\"_blank\">&lrm;Google Pay: Save and Pay</a></h4>\r\n\r\n<p><a href=\"https://apps.apple.com/us/app/google-pay-save-and-pay/id1193357041\" rel=\"nofollow\" target=\"_blank\">&lrm;Google Pay is a simple, secure, and seamless way to pay and save, giving you helpful ways to do more with your money: - Pay at your favorite places - Send and receive money instantly - Earn rewards for everyday payments - Discover offers from...</a></p>\r\n\r\n<p><a href=\"https://apps.apple.com/us/app/google-pay-save-and-pay/id1193357041\" rel=\"nofollow\" target=\"_blank\">&nbsp;apps.apple.com</a></p>\r\n\r\n<p>&nbsp;</p>', '../uploads/post/15-11-2022/6207002_Google_Wallet.jpg', 7, '2022-11-15 09:06:54', '2022-11-15 09:06:54', 1, 1),
(9, 'THỂ THAO  Man City tố Ronaldo nói dối', '<p>Ph&iacute;a Man City b&aacute;c bỏ tuy&ecirc;n bố của Cristiano Ronaldo về việc họ nỗ lực chi&ecirc;u mộ si&ecirc;u sao người Bồ Đ&agrave;o Nha trong h&egrave; 2021.</p>\r\n\r\n<p>Man City phủ nhận việc nỗ lực mang Ronaldo về s&acirc;n Etihad.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/mfnuy/2022_11_17/240519991_10216133723256225_1978029106442258390_n_201.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Man City phủ nhận việc nỗ lực mang Ronaldo về s&acirc;n Etihad.</p>\r\n\r\n<p><img alt=\"\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/mfnuy/2022_11_17/240519991_10216133723256225_1978029106442258390_n_201.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"\" src=\"https://znews-photo.zingcdn.me/w960/Uploaded/mfnuy/2022_11_17/240519991_10216133723256225_1978029106442258390_n_201.jpg\" title=\"null\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Man City phủ nhận việc nỗ lực mang Ronaldo về s&acirc;n Etihad.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Trong phần đầu ti&ecirc;n của cuộc phỏng vấn với Piers Morgan, Cristiano Ronaldo khẳng định được Man City li&ecirc;n hệ. &quot;T&ocirc;i đ&atilde; ở rất, rất gần Man City. Hai tuần trước khi quay lại MU, Pep Guardiola đ&atilde; gọi t&ocirc;i. City rất nỗ lực để c&oacute; t&ocirc;i. Nhưng anh biết đấy, t&ocirc;i c&oacute; qu&aacute; khứ với MU v&agrave; mối li&ecirc;n kết ấy đ&atilde; tạo ra kh&aacute;c biệt. Kh&aacute;c biệt nữa dĩ nhi&ecirc;n l&agrave; Sir Alex Ferguson&quot;, CR7 cho hay.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Man City b&aacute;c bỏ tuy&ecirc;n bố n&agrave;y. Theo&nbsp;<em>ESPN</em>&nbsp;v&agrave;&nbsp;<em>Manchester Evening News</em>, Man City cho rằng Ronaldo cung cấp th&ocirc;ng tin sai sự thật với Morgan. Ph&iacute;a Man City khẳng định những lời n&oacute;i của CR7 &quot;kh&ocirc;ng phản &aacute;nh&quot; sự quan t&acirc;m của họ v&agrave;o thời điểm đ&oacute;. &quot;The Citizens&quot; m&ocirc; tả mức độ lưu t&acirc;m của họ với Ronaldo chỉ dừng ở mức &quot;nhẹ nh&agrave;ng&quot;.</p>\r\n\r\n<p>Về l&yacute; do từ chối Man City, Ronaldo n&oacute;i th&ecirc;m: &quot;T&ocirc;i sẽ kh&ocirc;ng thể trung th&agrave;nh với MU nếu chuyển tới Man City. T&ocirc;i sẽ kh&ocirc;ng n&oacute;i Man City đến gần ra sao nhưng t&ocirc;i nghĩ bản th&acirc;n đ&atilde; đưa ra quyết định r&otilde; r&agrave;ng. T&ocirc;i kh&ocirc;ng hối hận ch&uacute;t n&agrave;o. Như anh n&oacute;i đấy, sir Alex Ferguson l&agrave; ch&igrave;a kh&oacute;a của chuyện n&agrave;y&quot;.</p>\r\n\r\n<p>Man City b&aacute;c tin li&ecirc;n hệ Ronaldo trong h&egrave; 2021.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;</li>\r\n	<li>&nbsp;</li>\r\n</ul>\r\n\r\n<p><img alt=\"Man City Ronaldo noi doi anh 1\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/mfnuy/2022_11_17/3207368_65702628_2560_1440_1642814200.jpg\" style=\"height:675px; width:1200px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Man City b&aacute;c tin li&ecirc;n hệ Ronaldo trong h&egrave; 2021.</p>\r\n\r\n<p><img alt=\"Man City Ronaldo noi doi anh 1\" src=\"https://znews-photo.zingcdn.me/w1920/Uploaded/mfnuy/2022_11_17/3207368_65702628_2560_1440_1642814200.jpg\" style=\"height:675px; width:1200px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Man City Ronaldo noi doi anh 1\" src=\"https://znews-photo.zingcdn.me/w960/Uploaded/mfnuy/2022_11_17/3207368_65702628_2560_1440_1642814200.jpg\" style=\"height:675px; width:1200px\" title=\"Man City Ronaldo nói dối ảnh 1\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Man City b&aacute;c tin li&ecirc;n hệ Ronaldo trong h&egrave; 2021.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>H&ocirc;m 14/11 (giờ H&agrave; Nội), ngay sau chiến thắng 2-1 của MU trước Fulham ở v&ograve;ng 16 Ngoại hạng Anh, Ronaldo g&acirc;y bất ngờ khi xuất hiện trong cuộc phỏng vấn với Morgan tr&ecirc;n&nbsp;<em>Talk TV</em>. Ở cuộc n&oacute;i chuyện, si&ecirc;u sao người Bồ Đ&agrave;o Nha tuy&ecirc;n bố kh&ocirc;ng t&ocirc;n trọng HLV Erik ten Hag v&agrave; nhiều th&agrave;nh vi&ecirc;n ban l&atilde;nh đạo MU.</p>\r\n\r\n<p>Một số nguồn tin tại Anh cho biết Ronaldo kh&ocirc;ng c&ograve;n đường trở về Old Trafford sau khi c&ocirc;ng khai chỉ tr&iacute;ch Man United. &quot;Quỷ đỏ&quot; sẵn s&agrave;ng b&aacute;n, thậm ch&iacute; thanh l&yacute; hợp đồng với tiền đạo 37 tuổi ngay khi kỳ chuyển nhượng tiếp theo mở cửa.</p>\r\n\r\n<p>C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, Ronaldo được cho đ&atilde; li&ecirc;n hệ với Bayern Munich để tự ứng cử cho một suất trong đội h&igrave;nh của HLV Julian Nagelsmann trong thời gian Sadio Mane gặp chấn thương. Tuy nhi&ecirc;n, nh&agrave; ĐKVĐ Bundesliga vẫn giữ lập trường từ kỳ chuyển nhượng h&egrave; 2022 l&agrave; kh&ocirc;ng chi&ecirc;u mộ Ronaldo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>00:02/01:16</p>\r\n\r\n<p>HD</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><a href=\"https://zingnews.vn/video-dang-sau-khoanh-khac-kho-chiu-cua-cancelo-voi-ronaldo-post1375624.html\" target=\"_blank\">Đằng sau khoảnh khắc kh&oacute; chịu của Cancelo với Ronaldo</a></strong>&nbsp;Khoảnh khắc căng thẳng của Joao Cancelo khi Cristiano Ronaldo tiếp cận đ&agrave;n em ở buổi tập của tuyển Bồ Đ&agrave;o Nha h&ocirc;m 15/11 đang g&acirc;y sốt tr&ecirc;n mạng x&atilde; hội.</p>', '../uploads/post/17-11-2022/240519991_10216133723256225_1978029106442258390_n_201.jpg', 8, '2022-11-17 20:39:47', '2022-11-17 20:39:47', 0, 1),
(18, '5 điều cần biết về Snapdragon 8 Gen 2: hiệu năng cực khủng, có Ray Tracing, hỗ trợ Wi-Fi 7', '<p>Rạng s&aacute;ng ng&agrave;y 16/11, Qualcomm đ&atilde; ch&iacute;nh thức ra mắt Snapdragon 8 Gen 2 &ndash; chipset cao cấp nhất của h&atilde;ng. Con chip mới n&agrave;y mang lại n&acirc;ng cấp mạnh mẽ về hiệu năng, khả năng ti&ecirc;u thụ pin v&agrave; rất nhiều c&ocirc;ng nghệ lần đầu xuất hiện. Dưới đ&acirc;y l&agrave; năm trong số những n&acirc;ng cấp th&uacute; vị nhất tr&ecirc;n Snapdragon 8 Gen 2.</p>\r\n\r\n<h2><strong>Hiệu năng vượt trội</strong></h2>\r\n\r\n<p>Một số h&igrave;nh ảnh r&ograve; rỉ gần đ&acirc;y đ&atilde; chụp lại kết quả b&agrave;i test benchmark vượt trội tr&ecirc;n Snapdragon 8 Gen 2. Cụ thể, h&igrave;nh ảnh dưới đ&acirc;y được chụp từ một thiết bị mang t&ecirc;n vivo V2227A, t&ecirc;n m&atilde; l&agrave; kalama v&agrave; chạy Android 13. M&aacute;y đạt hơn 1,29 triệu điểm Antutu Benchmark, 1.483 điểm đơn nh&acirc;n v&agrave; 4.709 điểm đa nh&acirc;n trong b&agrave;i test Geekbench 5.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Antutu-8Gen2.jpeg\" style=\"width:960px\" /></p>\r\n\r\n<p><strong><em>Điểm số tham khảo tr&ecirc;n Snapdragon 8 Gen 2</em></strong></p>\r\n\r\n<p>Con số n&agrave;y kh&aacute; tương đồng với kết quả cho ra tr&ecirc;n Dimensity 9200, một vi xử l&yacute; kh&aacute;c của MediaTek mới tr&igrave;nh l&agrave;ng gần đ&acirc;y. Đồng thời, vượt trội ho&agrave;n to&agrave;n so với nhiều đối thủ kh&aacute;c như Apple A16 Bionic, Exynos 2200 hay người tiền nhiệm Snapdragon 8+ Gen 1.</p>\r\n\r\n<p><strong><strong>T</strong>h&ocirc;ng số kỹ thuật tr&ecirc;n Snapdragon 8 Gen 2 v&agrave; một số đối thủ kh&aacute;c</strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td><strong>Snapdragon 8 Gen 2</strong></td>\r\n			<td><strong>Google Tensor G2</strong></td>\r\n			<td><strong>Exynos 2200</strong></td>\r\n			<td><strong>Apple A16 Bionic</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>CPU</strong></td>\r\n			<td>1x Cortex-X3 (3,2GHz)<br />\r\n			2x Cortex-A715 (2,8GHz)<br />\r\n			2x Cortex-A710 (2,8GHz)<br />\r\n			3x Cortex-A510 (2,0GHz)</td>\r\n			<td>2x Arm Cortex-X1 (2,85GHz)<br />\r\n			2x Arm Cortex-A78 (2,35GHz)<br />\r\n			4x Arm Cortex-A55 (1,80GHz)</td>\r\n			<td>1x Cortex-X2 (2,8GHz)<br />\r\n			3x Cortex-A710 (2,52GHz)<br />\r\n			4x Cortex-A510 (1,82GHz)</td>\r\n			<td>2x Everest (3,46GHz)<br />\r\n			4x Sawtooth (2,02GHz)</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>GPU</strong></td>\r\n			<td>Adreno 740</td>\r\n			<td>Mali-G710 MP7</td>\r\n			<td>Samsung Xclipse 920</td>\r\n			<td>Apple GPU (5 nh&acirc;n)</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Nh&acirc;n AI</strong></td>\r\n			<td>Hexagon</td>\r\n			<td>TPU thế hệ thứ hai</td>\r\n			<td>TPU hai nh&acirc;n</td>\r\n			<td>Neural Engine</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Modem mạng</strong></td>\r\n			<td>4G, 5G</td>\r\n			<td>4G, 5G</td>\r\n			<td>4G, 5G</td>\r\n			<td>4G, 5G</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Tiến tr&igrave;nh</strong></td>\r\n			<td>4nm TSMC</td>\r\n			<td>5nm Samsung</td>\r\n			<td>5nm Samsung</td>\r\n			<td>4nm TSMC</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>Hỗ trợ Ray Tracing</strong></h2>\r\n\r\n<p>Về cơ bản, Ray Tracing sử dụng hệ thống phần cứng chuy&ecirc;n biệt để xử l&yacute; c&aacute;c hiệu ứng, h&igrave;nh ảnh 3D trong c&aacute;c tựa game, khiến ch&uacute;ng trở n&ecirc;n sinh động v&agrave; tự nhi&ecirc;n hơn bao giờ hết. Qu&aacute; tr&igrave;nh t&iacute;nh to&aacute;n trong Ray Tracing được cho l&agrave; rất phức tạp, v&agrave; phải cần những phần cứng mạnh mẽ, ri&ecirc;ng biệt để xử l&yacute; ch&uacute;ng.</p>\r\n\r\n<p>Tr&ecirc;n PC, Ray Tracing xuất hiện tr&ecirc;n kh&ocirc;ng nhiều d&ograve;ng card đồ họa, chủ yếu l&agrave; c&aacute;c sản phẩm cao cấp của NVIDIA. C&ograve;n tr&ecirc;n điện thoại th&ocirc;ng minh, ch&uacute;ng cũng mới chỉ xuất hiện tr&ecirc;n số &iacute;t chipset, v&iacute; dụ như Exynos 2200 hay mới đ&acirc;y l&agrave; Dimensity 9200.</p>\r\n\r\n<h2><strong>Cấu tr&uacute;c CPU thay đổi</strong></h2>\r\n\r\n<p>Snapdragon 8 Gen 2 sử dụng cấu tr&uacute;c 1 &ndash; 4 &ndash; 3, bao gồm một nh&acirc;n hiệu năng cao Cortex-X3, bốn nh&acirc;n c&acirc;n bằng Cortex-A715 v&agrave; Cortex-A710, đi k&egrave;m với ba nh&acirc;n tiết kiệm điện Cortex-A510. Trong khi trước đ&oacute;, c&aacute;c thế hệ chipset tiền nhiệm như Snapdragon 8 Gen 1, Snapdragon 888 hay Snapdragon 865 đều sử dụng cấu tr&uacute;c nh&acirc;n 1 &ndash; 3 &ndash; 4, với một nh&acirc;n hiệu năng cao, ba nh&acirc;n c&acirc;n bằng v&agrave; bốn nh&acirc;n tiết kiệm điện.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Snapdragon-8-Gen-2-Specs-4.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<p>Điều n&agrave;y sẽ gi&uacute;p Snapdragon 8 Gen 2 xử l&yacute; nhanh ch&oacute;ng hơn c&aacute;c t&aacute;c vụ nhẹ v&agrave; trung b&igrave;nh, đổi lại phải hy sinh một phần nhiệt lượng v&agrave; điện năng ti&ecirc;u thụ. L&yacute; do đến từ việc chipset n&agrave;y đ&atilde; loại bỏ một nh&acirc;n tiết kiệm điện, thay v&agrave;o đ&oacute; l&agrave; một nh&acirc;n c&acirc;n bằng.</p>\r\n\r\n<h2><strong>Tăng cường sức mạnh AI</strong></h2>\r\n\r\n<p>Trong một v&agrave;i năm trở lại đ&acirc;y, AI (Artificial Intelligence) l&agrave; thuật ngữ xuất hiện phổ biến tr&ecirc;n điện thoại th&ocirc;ng minh n&oacute;i chung, v&agrave; CPU n&oacute;i ri&ecirc;ng. Việc trang bị một con chip với khả năng tận dụng AI tốt sẽ gi&uacute;p &iacute;ch rất nhiều cho c&aacute;c thuật to&aacute;n xử l&yacute; h&igrave;nh ảnh, ph&acirc;n phối hiệu năng hay tối ưu pin.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Snapdragon-8-Gen-2-Specs-2.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<p>Vi xử l&yacute; AI tr&ecirc;n Snapdragon 8 Gen 2, c&oacute; t&ecirc;n Hexagon, cũng nhận được rất nhiều n&acirc;ng cấp trong năm nay. Theo Android Authority, khả năng t&iacute;nh to&aacute;n tr&ecirc;n Hexagon nhanh hơn gấp đ&ocirc;i so với thế hệ trước đ&oacute;. Điều n&agrave;y c&oacute; nghĩa, bạn ho&agrave;n to&agrave;n c&oacute; thể tăng cường hiệu suất AI m&agrave; kh&ocirc;ng phải d&ugrave;ng t&agrave;i nguy&ecirc;n từ GPU hay CPU, gi&uacute;p con chip hoạt động hiệu quả v&agrave; tiết kiệm điện hơn.</p>\r\n\r\n<h2><strong>Hỗ trợ Wi-Fi 7</strong></h2>\r\n\r\n<p>Khi m&agrave; Wi-Fi 6 c&ograve;n chưa phổ biến tại Việt Nam, Qualcomm đ&atilde; trang bị tr&ecirc;n Snapdragon 8 Gen 2 khả năng hỗ trợ l&ecirc;n tới Wi-Fi 7. Chuẩn kết nối mới n&agrave;y được hứa hẹn cung cấp tốc độ nhanh hơn (tối đa 5,8Gbps), trong khi độ trễ được giảm thiểu đ&aacute;ng kể. Đ&aacute;ng n&oacute;i, theo intel.com, chuẩn Wi-Fi 7 c&ograve;n chưa được thương mại h&oacute;a, v&agrave; chưa c&oacute; bất kỳ modem mạng hay router n&agrave;o hỗ trợ t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://vatvostudio.vn/wp-content/uploads/2022/11/Snapdragon-8-Gen-2-Specs-1.jpg\" style=\"height:900px; width:1600px\" /></p>\r\n\r\n<p>Snapdragon 8 Gen 2 cũng được t&iacute;ch hợp modem 5G mới mang t&ecirc;n Snapdragon X70. Modem n&agrave;y được cho l&agrave; c&oacute; khả năng khai th&aacute;c sức mạnh của AI để cải thiện tốc độ truy xuất của 5G, trong khi giảm độ trễ v&agrave; tiết kiệm pin hơn. B&ecirc;n cạnh đ&oacute;, h&atilde;ng cũng nhấn mạnh rằng, Snapdragon 8 Gen 2 sẽ l&agrave; vi xử l&yacute; đầu ti&ecirc;n hỗ trợ hai SIM 5G hoạt động c&ugrave;ng l&uacute;c, tức l&agrave; bạn vừa c&oacute; thể gọi điện tr&ecirc;n SIM ch&iacute;nh, vừa c&oacute; thể nhận tin nhắn tr&ecirc;n SIM phụ một c&aacute;ch tức thời.</p>', '../uploads/post/17-11-2022/Snapdragon-8-Gen-2-Specs-3-1.jpg', 7, '2022-11-17 21:51:44', '2022-11-18 04:19:33', 0, 1);

